mapper.update:
UPDATE film_scene
SET seat_number=#{seatNumber}
WHERE film_scene_id=#{filmSceneId}

mapper.select:
SELECT c.*,b.price
from film_scene c
JOIN film1 b on b.id=c.film_id
Where film_id=#{filmScene.film_id}
  and cinema=#{filmScene.cinema}
  and date=#{filmScene.date}
  and begin_time=#{filmScene.begin_time}
  and cinema_type=#{filmScene.cinema_type}
